package loopsexample;

public class DoWhileExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int index=5;
		
		do
		{
		System.out.println(index +"Somthing");	
		index++;
		System.out.println("The Current value of the index variableis"+index);
		
		
		}while(index<=8);
		
		System.out.println("Condition is not satisfied");
		
		
		do
		{
		System.out.println(index+"Anything");
		index++;
		}while(index<=15);
		
		
		
		System.out.println();
		System.out.println("%%%%%%%%%%%%%%%%%%%%%%%");
		
		do
		{
		System.out.println(index +"vishnu");
		index=index-3;
		}while(index>=1);
	}
	
}

	
	
				
		
		

	


