package loopsexample;

public class ForLoopExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("WebDriver");
		System.out.println("WebDriver");
		System.out.println("WebDriver");
		System.out.println("WebDriver");
		System.out.println("WebDriver");
		
		
		for(int a=1;a<2;a++)
		{
		System.out.println("WebDriver");	
		}
		
		System.out.println("*********************");
		System.out.println();
		
		for(int a=1;a<2;a=a+1)
		{
			
		System.out.println("Selenium");
		}
		
		
		System.out.println("@@@@@@@@@@@@@@@@@@@@");
		System.out.println();
		
		for(int a=1;a<=2;a=a+1)
		{	
		System.out.println("Testing");
		}
		
		System.out.println("@@@@@@@@@@@@@@@@@@@@@@@@@@");
		System.out.println();
		for(int a=1;a<=5;a=a+2)
		{	
			System.out.println(a+"-"+"hello");
		}
		
		
		System.out.println("$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$");
		System.out.println();
		for(int a=1;a>=-3;a--)
		{
		System.out.println(a+"-"+"LoadRunner");	
		}
		
		System.out.println("##########################");
		System.out.println();
		for(int a=1;a>=-3;a=a-3)
		{
		System.out.println(a+"-"+"Jmeter");	
		}
		
		
		System.out.println("************************");
		System.out.println();
		
		for(int a=3;a>=-3;a=a-5)
		{
		System.out.println(a+"-"+"LiveTech");	
		}
	}
}

	

	
			
		
	
	
	
		
	
	
	

	
	
	
	
				
		
			

			
		

		
		
			
		
		
		
		
		 
		
		
	
		




