package loopsexample;

public class WhileLoopExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int var=2;
		
		while(var<=5)
		{
		//True Block
		System.out.println(var +"selenium");	
		var++;
		}
		
		System.out.println();
		System.out.println("****************");
		
		
		var=5;
		while(var<=10)
		{
		
		System.out.println();
		System.out.println("$$$$$$$$$$$$$$$$$$");
		
		
		System.out.println("The Current value of the variable var is:-"+var);
		
		while(var<=15)
		{
		//True Block
		System.out.println(var+"LoadRunner");
		var++;
		}
		
		
		System.out.println();
		System.out.println("$$$$$$$$$$$$$$$$$$$$$$$$");
		
		
		while(var>=14)
		{
		System.out.println("Jillebi");	
		var--;
		
		
		}
		}
	}
}

		
		
			

	


